mapper.select:
select count(1) from au_user

	
		and loginCode=#{logincode}
	
	
		and id!=#{id}

select count(1) from au_user

	
		and loginCode like concat('%',#{logincode},'%')
	
	
		and userName like concat('%',#{username},'%')
	
	
		and referCode like concat('%',#{refercode},'%')
	
	
	
		and roleId = #{roleid}
	
	
		and isStart = #{isstart}
	
	
		and referId = #{referid}
	
	
		and userType = #{usertype}

select * from au_user

	
		and loginCode like concat('%',#{logincode},'%')
	
	
		and userName like concat('%',#{username},'%')
	
	
		and referCode like concat('%',#{refercode},'%')
	
	
	
		and roleId = #{roleid}
	
	
		and isStart = #{isstart}
	
	
		and referId = #{referid}
	
	
		and userType = #{usertype}
	

order by createTime desc limit #{startIndex},#{pageSize}

mapper.update:
update au_user
idCardPicPath = NULL bankPicPath = NULL
where id=#{id}
